#include<iostream> #include<cstdio> #include<cmath> #include<string.h> using namespace std; int reverse(int a) { int r=0; for(;a!=0;) { r=r*10; r=r+a%10; a=a/10; } return r; } int main() { int p[3000]; int t,b,i,j; long long int a; cin>>t; for(i=0;i<t;i++) { cin>>a; if(a>0) { a=a+1; while(1) { b=reverse(a); if (b==a) break; else a=a+1; } p[i]=a; } } for(i=0;i<t;i++) { cout<<"\n"; cout<<p[i]; } }
